import React, { useState } from "react";
import Card from "../../../components/shared/Card/Card";
import Button from "../../../components/shared/Button/Button";
import styles from "./StepAvatar.module.css";
import { useSelector } from "react-redux";

const StepAvatar = ({ onNext }) => {
  const Name = useSelector((state) => state.activate.name);
  const [image, setImage] = useState("/images/avatar.webp");
  function submit() {}
  return (
    <>
      <Card title={`Okay ${Name}`} icon="monkey">
        <p className={styles.subHeading}>How's this photo</p>
        <div className={styles.avatarWrapper}>
          <img className={styles.avatar} src={image} alt="avatar" />
        </div>
        <div>
          <input id="avatarInput" type="file" className={styles.avatarInput} />
          <label htmlFor="avatarInput">Choose a different Photo</label>
        </div>
        <div>
          <Button onClick={submit} text="Next" />
        </div>
      </Card>
    </>
  );
};

export default StepAvatar;
